import React, { useEffect, useState } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Dashboard() {
  // メンバー関連の状態
  const [members, setMembers] = useState([]);
  const [memberForm, setMemberForm] = useState({ id: null, name: '', age: '', gender: '', department: '' });
  const [isEditingMember, setIsEditingMember] = useState(false);

  // アカウント関連の状態
  const [accounts, setAccounts] = useState([]);
  const [accountForm, setAccountForm] = useState({ id: null, accountId: '', password: '' });
  const [isEditingAccount, setIsEditingAccount] = useState(false);

  // データ取得
  const fetchMembers = () => {
    fetch('/api/members')
      .then(response => response.json())
      .then(data => setMembers(data))
      .catch(error => console.error('Error fetching members:', error));
  };

  const fetchAccounts = () => {
    fetch('/api/accounts')
      .then(response => response.json())
      .then(data => setAccounts(data))
      .catch(error => console.error('Error fetching accounts:', error));
  };

  useEffect(() => {
    fetchMembers();
    fetchAccounts();
  }, []);

  // --- メンバーCRUD ---
  const handleMemberSubmit = (e) => {
    e.preventDefault();
    if (isEditingMember) {
      fetch(`/api/members/${memberForm.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: memberForm.name,
          age: Number(memberForm.age),
          gender: memberForm.gender,
          department: memberForm.department
        })
      })
        .then(response => response.json())
        .then(updatedMember => {
          setMembers(members.map(m => m.id === updatedMember.id ? updatedMember : m));
          resetMemberForm();
        })
        .catch(error => console.error('Error updating member:', error));
    } else {
      fetch('/api/members', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: memberForm.name,
          age: Number(memberForm.age),
          gender: memberForm.gender,
          department: memberForm.department
        })
      })
        .then(response => response.json())
        .then(newMember => {
          setMembers([...members, newMember]);
          resetMemberForm();
        })
        .catch(error => console.error('Error creating member:', error));
    }
  };

  const handleMemberEdit = (member) => {
    setMemberForm(member);
    setIsEditingMember(true);
  };

  const handleMemberDelete = (id) => {
    fetch(`/api/members/${id}`, { method: 'DELETE' })
      .then(response => response.json())
      .then(deletedMember => {
        setMembers(members.filter(m => m.id !== deletedMember.id));
      })
      .catch(error => console.error('Error deleting member:', error));
  };

  const resetMemberForm = () => {
    setMemberForm({ id: null, name: '', age: '', gender: '', department: '' });
    setIsEditingMember(false);
  };

  // 部員データをもとに Chart.js 用の集計データを作成
  const departmentData = members.reduce((acc, member) => {
    acc[member.department] = (acc[member.department] || 0) + 1;
    return acc;
  }, {});

  const chartData = {
    labels: Object.keys(departmentData),
    datasets: [
      {
        label: 'メンバー数',
        data: Object.values(departmentData),
        backgroundColor: 'rgba(75, 192, 192, 0.6)'
      }
    ]
  };

  // --- アカウントCRUD ---
  const handleAccountSubmit = (e) => {
    e.preventDefault();
    if (isEditingAccount) {
      fetch(`/api/accounts/${accountForm.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          accountId: accountForm.accountId,
          password: accountForm.password
        })
      })
        .then(response => response.json())
        .then(updatedAccount => {
          setAccounts(accounts.map(a => a.id === updatedAccount.id ? updatedAccount : a));
          resetAccountForm();
        })
        .catch(error => console.error('Error updating account:', error));
    } else {
      fetch('/api/accounts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          accountId: accountForm.accountId,
          password: accountForm.password
        })
      })
        .then(response => response.json())
        .then(newAccount => {
          setAccounts([...accounts, newAccount]);
          resetAccountForm();
        })
        .catch(error => console.error('Error creating account:', error));
    }
  };

  const handleAccountEdit = (account) => {
    setAccountForm(account);
    setIsEditingAccount(true);
  };

  const handleAccountDelete = (id) => {
    fetch(`/api/accounts/${id}`, { method: 'DELETE' })
      .then(response => response.json())
      .then(deletedAccount => {
        setAccounts(accounts.filter(a => a.id !== deletedAccount.id));
      })
      .catch(error => console.error('Error deleting account:', error));
  };

  const resetAccountForm = () => {
    setAccountForm({ id: null, accountId: '', password: '' });
    setIsEditingAccount(false);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>ダッシュボード</h1>

      <section>
        <h2>メンバー管理</h2>
        <Bar data={chartData} />
        <form onSubmit={handleMemberSubmit}>
          <div>
            <label>名前: </label>
            <input
              type="text"
              value={memberForm.name}
              onChange={(e) => setMemberForm({ ...memberForm, name: e.target.value })}
              required
            />
          </div>
          <div>
            <label>年齢: </label>
            <input
              type="number"
              value={memberForm.age}
              onChange={(e) => setMemberForm({ ...memberForm, age: e.target.value })}
              required
            />
          </div>
          <div>
            <label>性別: </label>
            <input
              type="text"
              value={memberForm.gender}
              onChange={(e) => setMemberForm({ ...memberForm, gender: e.target.value })}
              required
            />
          </div>
          <div>
            <label>学科: </label>
            <input
              type="text"
              value={memberForm.department}
              onChange={(e) => setMemberForm({ ...memberForm, department: e.target.value })}
              required
            />
          </div>
          <button type="submit">{isEditingMember ? '更新' : '追加'}</button>
          {isEditingMember && <button type="button" onClick={resetMemberForm}>キャンセル</button>}
        </form>

        <ul>
          {members.map(member => (
            <li key={member.id}>
              {member.name} - {member.department} - {member.age}歳
              <button onClick={() => handleMemberEdit(member)} style={{ marginLeft: '10px' }}>
                編集
              </button>
              <button onClick={() => handleMemberDelete(member.id)} style={{ marginLeft: '5px' }}>
                削除
              </button>
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h2>アカウント管理</h2>
        <form onSubmit={handleAccountSubmit}>
          <div>
            <label>アカウントID: </label>
            <input
              type="text"
              value={accountForm.accountId}
              onChange={(e) => setAccountForm({ ...accountForm, accountId: e.target.value })}
              required
            />
          </div>
          <div>
            <label>パスワード: </label>
            <input
              type="password"
              value={accountForm.password}
              onChange={(e) => setAccountForm({ ...accountForm, password: e.target.value })}
              required
            />
          </div>
          <button type="submit">{isEditingAccount ? '更新' : '追加'}</button>
          {isEditingAccount && <button type="button" onClick={resetAccountForm}>キャンセル</button>}
        </form>

        <ul>
          {accounts.map(account => (
            <li key={account.id}>
              {account.accountId}
              <button onClick={() => handleAccountEdit(account)} style={{ marginLeft: '10px' }}>
                編集
              </button>
              <button onClick={() => handleAccountDelete(account.id)} style={{ marginLeft: '5px' }}>
                削除
              </button>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}

export default Dashboard;
