// client/src/MemberManagement.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function MemberManagement() {
  const navigate = useNavigate();
  const [members, setMembers] = useState([]);
  const [memberForm, setMemberForm] = useState({ id: null, name: '', age: '', gender: '', department: '' });
  const [isEditing, setIsEditing] = useState(false);

  // 部員情報の取得
  const fetchMembers = () => {
    fetch('/api/members')
      .then(response => response.json())
      .then(data => setMembers(data))
      .catch(error => console.error('Error fetching members:', error));
  };

  useEffect(() => {
    fetchMembers();
  }, []);

  // 追加 or 更新の処理
  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      fetch(`/api/members/${memberForm.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: memberForm.name,
          age: Number(memberForm.age),
          gender: memberForm.gender,
          department: memberForm.department
        })
      })
        .then(response => response.json())
        .then(updatedMember => {
          setMembers(members.map(m => m.id === updatedMember.id ? updatedMember : m));
          resetForm();
        })
        .catch(error => console.error('Error updating member:', error));
    } else {
      fetch('/api/members', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: memberForm.name,
          age: Number(memberForm.age),
          gender: memberForm.gender,
          department: memberForm.department
        })
      })
        .then(response => response.json())
        .then(newMember => {
          setMembers([...members, newMember]);
          resetForm();
        })
        .catch(error => console.error('Error creating member:', error));
    }
  };

  // 編集開始
  const handleEdit = (member) => {
    setMemberForm(member);
    setIsEditing(true);
  };

  // 削除処理
  const handleDelete = (id) => {
    fetch(`/api/members/${id}`, { method: 'DELETE' })
      .then(response => response.json())
      .then(deletedMember => {
        setMembers(members.filter(m => m.id !== deletedMember.id));
      })
      .catch(error => console.error('Error deleting member:', error));
  };

  // フォームリセット
  const resetForm = () => {
    setMemberForm({ id: null, name: '', age: '', gender: '', department: '' });
    setIsEditing(false);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>部員管理</h1>
      <button onClick={() => navigate('/dashboard')}>ダッシュボードに戻る</button>
      <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
        <div>
          <label>名前: </label>
          <input
            type="text"
            value={memberForm.name}
            onChange={(e) => setMemberForm({ ...memberForm, name: e.target.value })}
            required
          />
        </div>
        <div>
          <label>年齢: </label>
          <input
            type="number"
            value={memberForm.age}
            onChange={(e) => setMemberForm({ ...memberForm, age: e.target.value })}
            required
          />
        </div>
        <div>
          <label>性別: </label>
          <input
            type="text"
            value={memberForm.gender}
            onChange={(e) => setMemberForm({ ...memberForm, gender: e.target.value })}
            required
          />
        </div>
        <div>
          <label>学科: </label>
          <input
            type="text"
            value={memberForm.department}
            onChange={(e) => setMemberForm({ ...memberForm, department: e.target.value })}
            required
          />
        </div>
        <button type="submit">{isEditing ? '更新' : '追加'}</button>
        {isEditing && <button type="button" onClick={resetForm}>キャンセル</button>}
      </form>
      <h2 style={{ marginTop: '20px' }}>部員一覧</h2>
      <ul>
        {members.map(member => (
          <li key={member.id}>
            {member.name} - {member.department} - {member.age}歳
            <button onClick={() => handleEdit(member)} style={{ marginLeft: '10px' }}>編集</button>
            <button onClick={() => handleDelete(member.id)} style={{ marginLeft: '5px' }}>削除</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MemberManagement;
