// client/src/AccountManagement.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AccountManagement() {
  const navigate = useNavigate();

  // 現在のアカウント一覧を取得するための状態
  const [accounts, setAccounts] = useState([]);

  // 【新規作成】用の状態
  const [createAccountId, setCreateAccountId] = useState('');
  const [createPassword, setCreatePassword] = useState('');
  const [createMessage, setCreateMessage] = useState('');

  // 【更新】用の状態（対象のアカウントの内部ID、アカウント名、現在のパスワード、新しいパスワード）
  const [updateId, setUpdateId] = useState('');
  const [updateAccountId, setUpdateAccountId] = useState('');
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [updateMessage, setUpdateMessage] = useState('');

  // 【削除】用の状態（対象のアカウントの内部ID、アカウント名、現在のパスワード）
  const [deleteId, setDeleteId] = useState('');
  const [deleteAccountId, setDeleteAccountId] = useState('');
  const [deletePassword, setDeletePassword] = useState('');
  const [deleteMessage, setDeleteMessage] = useState('');

  // アカウント一覧の取得
  const fetchAccounts = () => {
    fetch('/api/accounts')
      .then(response => response.json())
      .then(data => setAccounts(data))
      .catch(error => console.error('Error fetching accounts:', error));
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  // 【新規作成】処理
  const handleCreate = (e) => {
    e.preventDefault();
    fetch('/api/accounts', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        accountId: createAccountId,
        password: createPassword
      })
    })
      .then(response => response.json())
      .then(data => {
        if (data.error) {
          setCreateMessage(`作成失敗: ${data.error}`);
        } else {
          setCreateMessage('アカウントが作成されました');
          setCreateAccountId('');
          setCreatePassword('');
          fetchAccounts();
        }
      })
      .catch(error => {
        setCreateMessage('作成中にエラーが発生しました');
      });
  };

  // 【更新】処理（パスワード更新）
  const handleUpdate = (e) => {
    e.preventDefault();
    // URLの:id は数値ID（内部ID）で指定
    fetch(`/api/accounts/${updateId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        accountId: updateAccountId,
        currentPassword,
        newPassword
      })
    })
      .then(response => response.json())
      .then(data => {
        if (data.error) {
          setUpdateMessage(`更新失敗: ${data.error}`);
        } else {
          setUpdateMessage('パスワードが更新されました');
          setUpdateId('');
          setUpdateAccountId('');
          setCurrentPassword('');
          setNewPassword('');
          fetchAccounts();
        }
      })
      .catch(error => {
        setUpdateMessage('更新中にエラーが発生しました');
      });
  };

  // 【削除】処理
  const handleDelete = (e) => {
    e.preventDefault();
    fetch(`/api/accounts/${deleteId}`, {
      method: 'DELETE',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        accountId: deleteAccountId,
        currentPassword: deletePassword
      })
    })
      .then(response => response.json())
      .then(data => {
        if (data.error) {
          setDeleteMessage(`削除失敗: ${data.error}`);
        } else {
          setDeleteMessage('アカウントが削除されました');
          setDeleteId('');
          setDeleteAccountId('');
          setDeletePassword('');
          fetchAccounts();
        }
      })
      .catch(error => {
        setDeleteMessage('削除中にエラーが発生しました');
      });
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>アカウント管理</h1>
      <button onClick={() => navigate('/dashboard')}>ダッシュボードに戻る</button>

      {/* 現在のアカウント一覧 */}
      <section style={{ marginTop: '20px' }}>
        <h2>現在のアカウント一覧</h2>
        <ul>
          {accounts.map(acc => (
            <li key={acc.id}>
              ID: {acc.id} / アカウントID: {acc.accountId}
            </li>
          ))}
        </ul>
      </section>

      {/* 新規アカウント作成エリア */}
      <section style={{ marginTop: '20px', border: '1px solid #ccc', padding: '10px' }}>
        <h2>新規アカウント作成</h2>
        <form onSubmit={handleCreate}>
          <div>
            <label>アカウントID: </label>
            <input
              type="text"
              value={createAccountId}
              onChange={(e) => setCreateAccountId(e.target.value)}
              required
            />
          </div>
          <div>
            <label>パスワード: </label>
            <input
              type="password"
              value={createPassword}
              onChange={(e) => setCreatePassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">作成</button>
        </form>
        {createMessage && <p>{createMessage}</p>}
      </section>

      {/* パスワード更新エリア */}
      <section style={{ marginTop: '20px', border: '1px solid #ccc', padding: '10px' }}>
        <h2>パスワード更新</h2>
        <form onSubmit={handleUpdate}>
          <div>
            <label>対象のアカウントID (数値ID): </label>
            <input
              type="number"
              value={updateId}
              onChange={(e) => setUpdateId(e.target.value)}
              required
            />
          </div>
          <div>
            <label>アカウント名: </label>
            <input
              type="text"
              value={updateAccountId}
              onChange={(e) => setUpdateAccountId(e.target.value)}
              required
            />
          </div>
          <div>
            <label>現在のパスワード: </label>
            <input
              type="password"
              value={currentPassword}
              onChange={(e) => setCurrentPassword(e.target.value)}
              required
            />
          </div>
          <div>
            <label>新しいパスワード: </label>
            <input
              type="password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">更新</button>
        </form>
        {updateMessage && <p>{updateMessage}</p>}
      </section>

      {/* アカウント削除エリア */}
      <section style={{ marginTop: '20px', border: '1px solid #ccc', padding: '10px' }}>
        <h2>アカウント削除</h2>
        <form onSubmit={handleDelete}>
          <div>
            <label>対象のアカウントID (数値ID): </label>
            <input
              type="number"
              value={deleteId}
              onChange={(e) => setDeleteId(e.target.value)}
              required
            />
          </div>
          <div>
            <label>アカウント名: </label>
            <input
              type="text"
              value={deleteAccountId}
              onChange={(e) => setDeleteAccountId(e.target.value)}
              required
            />
          </div>
          <div>
            <label>現在のパスワード: </label>
            <input
              type="password"
              value={deletePassword}
              onChange={(e) => setDeletePassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">削除</button>
        </form>
        {deleteMessage && <p>{deleteMessage}</p>}
      </section>
    </div>
  );
}

export default AccountManagement;
