// client/src/MemberManagement.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function MemberManagement({ operator }) {
  const navigate = useNavigate();
  const [members, setMembers] = useState([]);
  const [memberForm, setMemberForm] = useState({
    id: null,
    name: '',
    age: '',
    gender: '',
    department: ''
  });
  const [isEditing, setIsEditing] = useState(false);
  const [message, setMessage] = useState('');

  // 操作ログを記録する状態（配列）
  const [logs, setLogs] = useState([]);

  // 部員一覧の取得
  const fetchMembers = () => {
    fetch('/api/members')
      .then((response) => response.json())
      .then((data) => setMembers(data))
      .catch((error) => console.error('Error fetching members:', error));
  };

  useEffect(() => {
    fetchMembers();
  }, []);

  // フォーム送信（新規作成 or 更新）
  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      // 編集の場合：PUT リクエスト
      fetch(`/api/members/${memberForm.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: memberForm.name,
          age: Number(memberForm.age),
          gender: memberForm.gender,
          department: memberForm.department
        })
      })
        .then((response) => response.json())
        .then((updatedMember) => {
          setMembers(members.map((m) => (m.id === updatedMember.id ? updatedMember : m)));
          setMessage('部員情報が更新されました');
          // ログに記録（例：「operator が [member name] の編集を実施」）
          setLogs((prevLogs) => [
            ...prevLogs,
            `${operator} が "${updatedMember.name}" の編集を実施しました`
          ]);
          resetForm();
        })
        .catch((error) => console.error('Error updating member:', error));
    } else {
      // 新規追加の場合：POST リクエスト
      fetch('/api/members', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: memberForm.name,
          age: Number(memberForm.age),
          gender: memberForm.gender,
          department: memberForm.department
        })
      })
        .then((response) => response.json())
        .then((newMember) => {
          setMembers([...members, newMember]);
          setMessage('部員情報が追加されました');
          // ログに記録（例：「operator が [member name] を追加」）
          setLogs((prevLogs) => [
            ...prevLogs,
            `${operator} が "${newMember.name}" を追加しました`
          ]);
          resetForm();
        })
        .catch((error) => console.error('Error creating member:', error));
    }
  };

  // 編集ボタン押下時：対象部員の情報をフォームにセット
  const handleEdit = (member) => {
    setMemberForm(member);
    setIsEditing(true);
  };

  // 削除処理
  const handleDelete = (id) => {
    fetch(`/api/members/${id}`, { method: 'DELETE' })
      .then((response) => response.json())
      .then((deletedMember) => {
        setMembers(members.filter((m) => m.id !== deletedMember.id));
        setMessage('部員情報が削除されました');
        // ログに記録（例：「operator が [member name] を削除」）
        setLogs((prevLogs) => [
          ...prevLogs,
          `${operator} が "${deletedMember.name}" を削除しました`
        ]);
      })
      .catch((error) => console.error('Error deleting member:', error));
  };

  // フォームリセット
  const resetForm = () => {
    setMemberForm({ id: null, name: '', age: '', gender: '', department: '' });
    setIsEditing(false);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>部員管理</h1>
      <button onClick={() => navigate('/dashboard')}>ダッシュボードに戻る</button>
      <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
        <div>
          <label>名前: </label>
          <input
            type="text"
            value={memberForm.name}
            onChange={(e) => setMemberForm({ ...memberForm, name: e.target.value })}
            required
          />
        </div>
        <div>
          <label>年齢: </label>
          <input
            type="number"
            value={memberForm.age}
            onChange={(e) => setMemberForm({ ...memberForm, age: e.target.value })}
            required
          />
        </div>
        <div>
          <label>性別: </label>
          <input
            type="text"
            value={memberForm.gender}
            onChange={(e) => setMemberForm({ ...memberForm, gender: e.target.value })}
            required
          />
        </div>
        <div>
          <label>学科: </label>
          <input
            type="text"
            value={memberForm.department}
            onChange={(e) => setMemberForm({ ...memberForm, department: e.target.value })}
            required
          />
        </div>
        <button type="submit">{isEditing ? '更新' : '追加'}</button>
        {isEditing && <button type="button" onClick={resetForm}>キャンセル</button>}
      </form>
      {message && <p>{message}</p>}
      <h2 style={{ marginTop: '20px' }}>部員一覧</h2>
      <ul>
        {members.map((member) => (
          <li key={member.id}>
            {member.name} - {member.department} - {member.age}歳
            <button onClick={() => handleEdit(member)} style={{ marginLeft: '10px' }}>
              編集
            </button>
            <button onClick={() => handleDelete(member.id)} style={{ marginLeft: '5px' }}>
              削除
            </button>
          </li>
        ))}
      </ul>
      <h2 style={{ marginTop: '20px' }}>操作ログ</h2>
      <ul>
        {logs.map((log, index) => (
          <li key={index}>{log}</li>
        ))}
      </ul>
    </div>
  );
}

export default MemberManagement;
