// client/src/MemberManagement.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function MemberManagement() {
  const navigate = useNavigate();

  // 部員一覧とログの状態
  const [members, setMembers] = useState([]);
  const [logs, setLogs] = useState([]);

  // 部員追加／編集用フォームの状態
  const [form, setForm] = useState({
    id: null,
    name: '',
    age: '',
    gender: '',
    department: ''
  });
  const [isEditing, setIsEditing] = useState(false);

  // 操作実行者（ログに記録されるユーザー名）
  const [operator, setOperator] = useState('');

  // メッセージ表示用
  const [message, setMessage] = useState('');

  // 部員一覧取得
  const fetchMembers = () => {
    fetch('/api/members')
      .then(res => res.json())
      .then(data => setMembers(data))
      .catch(err => console.error('Error fetching members:', err));
  };

  // 操作ログ取得
  const fetchLogs = () => {
    fetch('/api/members/logs')
      .then(res => res.json())
      .then(data => setLogs(data))
      .catch(err => console.error('Error fetching logs:', err));
  };

  useEffect(() => {
    fetchMembers();
    fetchLogs();
  }, []);

  // 新規追加または更新処理
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!operator.trim()) {
      setMessage('操作実行者を入力してください');
      return;
    }

    if (isEditing) {
      // 編集の場合、PUT /api/members/:id を使用
      fetch(`/api/members/${form.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...form, operator })
      })
        .then(res => res.json())
        .then(data => {
          if (data.error) {
            setMessage(`更新失敗: ${data.error}`);
          } else {
            setMessage('部員情報の更新に成功しました');
            setForm({ id: null, name: '', age: '', gender: '', department: '' });
            setIsEditing(false);
            fetchMembers();
            fetchLogs();
          }
        })
        .catch(err => setMessage('更新中にエラーが発生しました'));
    } else {
      // 追加の場合、POST /api/members を使用
      fetch('/api/members', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...form, operator })
      })
        .then(res => res.json())
        .then(data => {
          if (data.error) {
            setMessage(`追加失敗: ${data.error}`);
          } else {
            setMessage('部員情報の追加に成功しました');
            setForm({ id: null, name: '', age: '', gender: '', department: '' });
            fetchMembers();
            fetchLogs();
          }
        })
        .catch(err => setMessage('追加中にエラーが発生しました'));
    }
  };

  // 編集開始：対象の部員情報をフォームに読み込む
  const handleEdit = (member) => {
    setForm(member);
    setIsEditing(true);
    setMessage('');
  };

  // 削除処理（DELETE /api/members/:id を使用）
  const handleDelete = (member) => {
    if (!operator.trim()) {
      setMessage('操作実行者を入力してください');
      return;
    }
    fetch(`/api/members/${member.id}`, {
      method: 'DELETE',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ operator })
    })
      .then(res => res.json())
      .then(data => {
        if (data.error) {
          setMessage(`削除失敗: ${data.error}`);
        } else {
          setMessage('部員情報の削除に成功しました');
          fetchMembers();
          fetchLogs();
        }
      })
      .catch(err => setMessage('削除中にエラーが発生しました'));
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>部員管理</h1>
      <button onClick={() => navigate('/dashboard')}>ダッシュボードに戻る</button>

      {/* 操作実行者の入力 */}
      <div style={{ margin: '20px 0' }}>
        <label>操作実行者 (Operator): </label>
        <input
          type="text"
          value={operator}
          onChange={(e) => setOperator(e.target.value)}
          placeholder="例: demo"
          required
        />
      </div>

      {/* 部員一覧表示 */}
      <h2>部員一覧</h2>
      <ul>
        {members.map(member => (
          <li key={member.id}>
            {member.name} - {member.department} - {member.age}歳
            <button onClick={() => handleEdit(member)} style={{ marginLeft: '10px' }}>編集</button>
            <button onClick={() => handleDelete(member)} style={{ marginLeft: '5px' }}>削除</button>
          </li>
        ))}
      </ul>

      {/* 部員追加／更新フォーム */}
      <h2>{isEditing ? '部員情報更新' : '新規部員追加'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>名前: </label>
          <input
            type="text"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />
        </div>
        <div>
          <label>年齢: </label>
          <input
            type="number"
            value={form.age}
            onChange={(e) => setForm({ ...form, age: e.target.value })}
            required
          />
        </div>
        <div>
          <label>性別: </label>
          <input
            type="text"
            value={form.gender}
            onChange={(e) => setForm({ ...form, gender: e.target.value })}
            required
          />
        </div>
        <div>
          <label>学科: </label>
          <input
            type="text"
            value={form.department}
            onChange={(e) => setForm({ ...form, department: e.target.value })}
            required
          />
        </div>
        <button type="submit">{isEditing ? '更新' : '追加'}</button>
      </form>
      {message && <p>{message}</p>}

      {/* 操作ログの表示 */}
      <h2>操作ログ</h2>
      {logs.length > 0 ? (
        <ul>
          {logs.map((log, index) => (
            <li key={index}>
              [{log.timestamp}] {log.operator} が {log.target} に対して {log.action} を行いました
            </li>
          ))}
        </ul>
      ) : (
        <p>ログがありません</p>
      )}
    </div>
  );
}

export default MemberManagement;
