import React, { useEffect, useState } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function App() {
  const [members, setMembers] = useState([]);
  // フォーム用の状態。id が null の場合は新規作成、値がある場合は更新モード
  const [form, setForm] = useState({ id: null, name: '', age: '', gender: '', department: '' });
  const [isEditing, setIsEditing] = useState(false);

  // コンポーネントの初回マウント時にメンバー一覧を取得
  useEffect(() => {
    fetchMembers();
  }, []);

  const fetchMembers = () => {
    fetch('/api/members')
      .then(response => response.json())
      .then(data => setMembers(data))
      .catch(error => console.error('Error fetching members:', error));
  };

  // フォーム送信時の処理：新規作成または更新の判断を行う
  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      // 更新の場合：PUT リクエスト
      fetch(`/api/members/${form.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: form.name,
          age: Number(form.age),
          gender: form.gender,
          department: form.department
        })
      })
        .then(response => response.json())
        .then(updatedMember => {
          // state のメンバー情報を更新
          setMembers(members.map(member => member.id === updatedMember.id ? updatedMember : member));
          resetForm();
        })
        .catch(error => console.error('Error updating member:', error));
    } else {
      // 新規作成の場合：POST リクエスト
      fetch('/api/members', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: form.name,
          age: Number(form.age),
          gender: form.gender,
          department: form.department
        })
      })
        .then(response => response.json())
        .then(newMember => {
          setMembers([...members, newMember]);
          resetForm();
        })
        .catch(error => console.error('Error creating member:', error));
    }
  };

  // 編集ボタン押下時の処理：対象のメンバー情報をフォームに反映
  const handleEdit = (member) => {
    setForm(member);
    setIsEditing(true);
  };

  // 削除ボタン押下時の処理：DELETE リクエストを送信
  const handleDelete = (id) => {
    fetch(`/api/members/${id}`, { method: 'DELETE' })
      .then(response => response.json())
      .then(deletedMember => {
        setMembers(members.filter(member => member.id !== deletedMember.id));
      })
      .catch(error => console.error('Error deleting member:', error));
  };

  // フォームをリセットし、編集モードを解除
  const resetForm = () => {
    setForm({ id: null, name: '', age: '', gender: '', department: '' });
    setIsEditing(false);
  };

  // Chart.js 用：学科ごとのメンバー数を集計
  const departmentData = members.reduce((acc, member) => {
    acc[member.department] = (acc[member.department] || 0) + 1;
    return acc;
  }, {});

  const chartData = {
    labels: Object.keys(departmentData),
    datasets: [
      {
        label: 'メンバー数',
        data: Object.values(departmentData),
        backgroundColor: 'rgba(75, 192, 192, 0.6)'
      }
    ]
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>メンバー管理アプリ</h1>
      <Bar data={chartData} />

      <h2>{isEditing ? 'メンバー更新' : '新規メンバー追加'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>名前: </label>
          <input
            type="text"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />
        </div>
        <div>
          <label>年齢: </label>
          <input
            type="number"
            value={form.age}
            onChange={(e) => setForm({ ...form, age: e.target.value })}
            required
          />
        </div>
        <div>
          <label>性別: </label>
          <input
            type="text"
            value={form.gender}
            onChange={(e) => setForm({ ...form, gender: e.target.value })}
            required
          />
        </div>
        <div>
          <label>学科: </label>
          <input
            type="text"
            value={form.department}
            onChange={(e) => setForm({ ...form, department: e.target.value })}
            required
          />
        </div>
        <button type="submit">{isEditing ? '更新' : '追加'}</button>
        {isEditing && <button type="button" onClick={resetForm}>キャンセル</button>}
      </form>

      <h2>メンバー一覧</h2>
      <ul>
        {members.map(member => (
          <li key={member.id}>
            {member.name} - {member.department} - {member.age}歳
            <button onClick={() => handleEdit(member)} style={{ marginLeft: '10px' }}>
              編集
            </button>
            <button onClick={() => handleDelete(member.id)} style={{ marginLeft: '5px' }}>
              削除
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
